SELECT DISTINCT concepts_common.inauthority,substring(refname from position('''' in refname) + 1 for char_length(refname) - position('''' in refname) - 1), h.name FROM concepts_common JOIN hierarchy h on h.id = concepts_common.id LEFT OUTER JOIN misc  ON h.id = misc.id WHERE misc.lifecyclestate <> 'deleted' ORDER BY concepts_common.inauthority;
